import React, { Component } from 'react';

class Game extends Component {
    state = {
        gravity: 0.8,
        bird: {
            x: 50,
            y: 100,
            verocity: 0,
            radius: 20
        }
    }
    draw = () => {
        const ctx = this.refs.canvas.getContext("2d");
        // change this colour to change the colour of your 
        // "pen" in the canvas 
        ctx.fillStyle = "pink";
        ctx.beginPath();
        ctx.arc(this.state.bird.x, this.state.bird.y,                        
                this.state.bird.radius, 0, 2 * Math.PI);
        ctx.fill();
        ctx.stroke();
    }
    update = () => {
        let newV = (this.state.bird.velocity + this.state.gravity) * 0.9
        this.setState({
            bird: {
                x: 50,
                y: Math.max(
                    this.state.bird.y + newV,
                    this.refs.canvas.height - this.state
                )
            }
        })
    }
    componentDidMount() {
        setInterval(() => {
            this.update();
            this.draw();
        }, 1000 / 60);
    }

    render() {
        return (
            <div> 
              <canvas ref="canvas" width={450} height={650} />
           </div>
        );
    }
}
export default Game;
