import React, { Component } from 'react';
class Game extends Component {
    state = {
        gravity: 0.8,
        lift: -15,
        bird: {
            x: 1000,
            y: 100,
            velocity: 0,
            radius: 40
        }
    }


draw = () => {
    const ctx = this.refs.canvas.getContext("2d");
    // change this colour to change the colour of your 
    // "pen" in the canvas 
    // ctx.fillStyle = "green";
    ctx.fillStyle = "pink";
    ctx.fillRect(2, 20, this.refs.canvas.width,   
        this.refs.canvas.height);     
    ctx.beginPath();

    ctx.font = "30px Arial";
    ctx.strokeText("Welcome to The HeidtMoore", 10, 50);

    ctx.arc(this.state.bird.x, this.state.bird.y,                        
            this.state.bird.radius, 0, 2 * Math.PI);
    ctx.fill();
    ctx.stroke();
}

update = () => {
    let newV = (this.state.bird.velocity + this.state.gravity) * 0.9
    this.setState({
      bird: {
        x: 200,
        y: Math.max(
          Math.min(
            this.state.bird.y + newV,
            this.refs.canvas.height - this.state.bird.radius
          ),
          0
        ),
        velocity: newV,
        radius: 40
      }
    });
}

componentDidMount() {
    setInterval(() => {
        this.update();
        this.draw();
    }, 1000 / 60);
    document.addEventListener("keydown", e =>
    e.keyCode === 32 ? this.setState({ 
      bird: {
        x: 50,
        y: this.state.bird.y,
        velocity: this.state.bird.velocity + this.state.lift,
        radius: 20
        }
      }) : null
  );
}

    render() {
        return (
            <div> 

            <canvas ref="canvas" width={550} height={600} />

            </div>
        );
    }
}

export default Game;
